AHK:
; AutoHotkey

Page := 1
WB.document.parentWindow.ahk_next_page := Func("Next")
WB.document.parentWindow.ahk_prev_page := Func("Prev")

Next()
{
	global Page
	ShowPage(Page := Clamp(Page+1, 1, 4))
}

Prev()
{
	global Page
	ShowPage(Page := Clamp(Page-1, 1, 4))
}

ShowPage(Page)
{
	global WB	
	Pages := WB.document.getElementsByClassName("page")
	Loop, % Pages.Length
			Pages[A_Index-1].hidden := (A_Index == Page)-1
}

Clamp(Val, Min, Max)
{
	return Val<Min?Min:Val>Max?Max:Val
}

